mapper.select:
SELECT * FROM roommatechat WHERE sendUserType = #{userType} AND sendUserNum = #{userNum} AND receiveUserType = #{receiveuserType} AND receiveuserNum = #{receiveuserNum}
	UNION
	SELECT * FROM roommatechat WHERE sendUserType = #{receiveuserType} AND sendUserNum = #{receiveuserNum} AND receiveUserType = #{userType} AND receiveuserNum = #{userNum}
	ORDER BY textDate;
